rule "mailcow_dovecot_grok"
when
  has_field("container_name") AND contains("mailcowdockerized_dovecot-mailcow_1",to_string($message.container_name))
then
  let original_message = to_string($message.message);
  let parsed_field = grok(pattern: "%{DOVECOT}", value: original_message, only_named_captures: true);
  set_fields(parsed_field);
end

rule "mailcow_netfilter_ip_details"
when
  has_field("netfilter_ip")
then
  let geo = lookup("geo-ip-city-maxmind-lookup", to_string($message.netfilter_ip));
  set_field("netfilter_ip_geolocation", geo["coordinates"]);
  set_field("netfilter_ip_geo_country_code", geo["country"].iso_code);
  set_field("netfilter_ip_geo_country_name", geo["country"].names.en);
  set_field("netfilter_ip_geo_city_name", geo["city"].names.en);

  let whoice = lookup("ip-whoice", to_ip($message.netfilter_ip));
  set_field("netfilter_ip_organisation", whoice["multi_value"].organization);

end

